library(shiny)
library(DT)

# Your small dataset, assuming it's a data frame called 'df'
df <- data.frame(
  col1 = c("value1", "value2", "value3"), 
  col2 = c(1, 2, 3),
  col3 = c(TRUE, FALSE, TRUE)
)

ui <- fluidPage(
  titlePanel("My Shiny App with DT Table"),
  mainPanel(
    DT::dataTableOutput("table")
  )
)

server <- function(input, output) {
  output$table <- DT::renderDataTable({
    DT::datatable(df)
  })
}

shinyApp(ui, server)
